// Helper function to get today's content
export function getTodayContent(): DailyContent | null {
    const today = new Date();
    const key = `${String(today.getMonth() + 1).padStart(2, '0')}-${String(today.getDate()).padStart(2, '0')}`;
    return dailyContentData[key] || dailyContentData['12-09']; // Fallback to Adam
}
